import React from "react";
import { Button, Icon, TextInput } from "react-materialize";
import { Link } from "react-router-dom";

function Card(props) {
  return (
    <div className="col s6">
      <div className="card sticky-action">
        <div className="card-image waves-effect waves-block waves-light">
          <img
            className="img-fluid"
            src={props.advertisement.image}
            style={{ height: "200px", width: "400px" }}
          />
        </div>
        <div className="card-action">
          <span className="card-title activator grey-text text-darken-4">
            {props.advertisement.description}
            <i className="material-icons right">more_vert</i>
          </span>
          <p>
            <a href="#">{props.advertisement.price}</a>
          </p>
          <p>
            <a href="#">
              {new Date(props.advertisement.date).toLocaleDateString()}
            </a>
          </p>
        </div>
        <div className="card-reveal">
          <span className="card-title grey-text text-darken-4">
            Description<i className="material-icons right">close</i>
          </span>
          <p>{props.advertisement.description}</p>
        </div>
      </div>
    </div>
  );
}

export default Card;
