              </form>
            </div>
          </nav>
        </div>

        {search == "" ? (
          <div>
            <Carousell />
            {/*<div className="col s6 offset-m3">*/}

            {/*  <br/>*/}
            {/*  <br/>*/}
            {/*  <div className="card yellow lighten-3">*/}
            {/*  */}
            {/*    <div className="card-content">*/}
