import "./App.css";
import "materialize-css/dist/css/materialize.css";
import "materialize-css/dist/js/materialize.js";

function App() {
  const [name, setName] = useState("");
  const [message, setMessage] = useState("");
  const [newId, setNewId] = useState(0);
  const [search, setSearch] = useState("");

  const handleSearchChange = (event) => {
    setSearch(event.target.value);
  };

  const handleNameChange = (event) => {
    setName(event.target.value);
  };

  const handleSearchSubmit = (event) => {};

  const handleSubmit = (event) => {
    var date = new Date();

    const inputData = { name, date };
    addCard(inputData);
    setName("");

    event.preventDefault();
  };

  async function addCard(data) {
    const response = await fetch("http://localhost:8000/api/saveTodo", {
      method: "POST",
      mode: "cors",
      cache: "no-cache",
      credentials: "same-origin",
      headers: {
        "Content-Type": "application/json",
        Authorization: bearer,
      },
      redirect: "follow",
      referrerPolicy: "no-referrer",
      body: JSON.stringify(data),
    });
    let messData = await response.json();
    setMessage(messData.id ? "Data Added : " : "Error");
    console.log(messData);

    setNewId(messData.id);
  }

  return (
    <div className="container" style={{ minHeight: "407px" }}>
      <div className="row">
        <div>
          <AllCards newCardAddedId={newId} />
        </div>
      </div>
    </div>
  );
}

export default App;
